insert into questions (id, question_order, question_text,mandatory, justify, type, hint, activity_id ) values ('5826c960-ca37-459d-ac1c-92525000cfdf', 0, '¿La empresa ha implementado el desarrollo de nuevos conceptos?', true, true, 'SINGLE_CHOICE_DEPENDENT', 'Etapa de diseño en la que se toman decisiones oportunas sobre los impactos del productos teniendo en cuenta la función que el producto va a desempeñar. Se tiene en cuenta la desmaterialización, la multifuncionalidad, el uso compartido del producto, y el servicio en lugar de producto.', '6927b0b2-ddba-453d-865b-a2ffb573fc77');
