insert into questions (id, question_order, question_text,mandatory, justify, type, hint, activity_id ) values ('4a71698d-e962-4d8e-b67b-b0c68b6a5c6d', 1, '¿La empresa ha seleccionado materiales de menor impacto ambiental?', true, true, 'SINGLE_CHOICE_DEPENDENT', 'Adoptar el uso de materiales reciclables, libres de sustancias peligrosas, ecológicos, locales. Se da preferencia a productos de “un solo material” para facilitar el reciclado. Evitar materiales contaminantes o muy intensivos en el uso de energía.', '6927b0b2-ddba-453d-865b-a2ffb573fc77');
