insert into questions (id, question_order, question_text,mandatory, justify, type, hint, activity_id ) values ('96f781d1-81aa-4c7c-bdf0-5c20d73fba98', 2, '¿La empresa ha implementado la reducción del peso o volumen de los materiales?', true, true, 'SINGLE_CHOICE_DEPENDENT', 'Llevar al punto óptimo la cantidad de recursos que requiere el producto, ya sea por peso o volumen. Considerar, la utilización de sistemas plegables o apilables.', '6927b0b2-ddba-453d-865b-a2ffb573fc77');
