insert into questions (id, question_order, question_text,mandatory, justify, type, hint, activity_id ) values ('bf0088bc-c11b-44fe-b00f-637cd6108a79', 3, ' ¿La empresa ha implementado la optimización del proceso productivo?', true, true, 'SINGLE_CHOICE_DEPENDENT', 'Reducir el impacto del proceso productivo utilizando métodos o técnicas mas limpias, con menores desperdicios o menor consumo de recursos materiales o naturales como agua o energía.', '6927b0b2-ddba-453d-865b-a2ffb573fc77');
