insert into questions (id, question_order, question_text,mandatory, justify, type, hint, activity_id ) values ('ded5761c-a52f-4bb9-b148-c7dd2a84f4e5', 4, '¿La empresa ha realizado optimización de la distribución?', true, true, 'SINGLE_CHOICE_DEPENDENT', 'En la cadena de abastecimiento tener en cuenta aspectos como el embalaje y los medios de transporte utilizados. Se hace un mejor ruteo, se optimiza la carga de envíos, la cantidad de material usado el embalaje y su gestión posterior.', '6927b0b2-ddba-453d-865b-a2ffb573fc77');
