insert into questions (id, question_order, question_text,mandatory, justify, type, hint, activity_id ) values ('d0a55be1-5c68-4c38-8cce-68ca4c5f5748', 5, '¿La empresa ha implementado la reducción de impactos durante el uso del producto?', true, true, 'SINGLE_CHOICE_DEPENDENT', 'Se tiene en cuenta el futuro del producto, incluye su mantenimiento. Así, se diseña el producto para que requiera menos mantenimiento, se reduzca los recursos o insumos requeridos para su uso.', '6927b0b2-ddba-453d-865b-a2ffb573fc77');
