insert into questions (id, question_order, question_text,mandatory, justify, type, hint, activity_id ) values ('46f0fcc0-95df-405a-839d-eaa441bd822f', 6, '¿La empresa ha implementado la extensión de la vida útil del producto?', true, true, 'SINGLE_CHOICE_DEPENDENT', 'Se diseña un producto con mayor durabilidad manteniendo su funcionalidad. En otras palabras, se evita que el producto sea rápidamente reemplazado y así se reducen los residuos. Se aumenta la confiabilidad y posibilidades de reparación del producto.', '6927b0b2-ddba-453d-865b-a2ffb573fc77');
