insert into questions (id, question_order, question_text,mandatory, justify, type, hint, activity_id ) values ('6650564d-512f-4db9-b851-9f5622fc03aa', 7, '¿La empresa ha optimizado el fin de vida del producto?', true, true, 'SINGLE_CHOICE_DEPENDENT', 'El diseño del producto concibe la etapa del producto en la que se considera un residuo, es decir, cuando ya no tiene uso. Considera el diseño para el desensamble, separación para posterior recuperación o disposición.', '6927b0b2-ddba-453d-865b-a2ffb573fc77');
